import React, { useState, Component } from 'react';
import './App.css';
import { Layout, Avatar, Menu, Icon, Breadcrumb, Button } from 'antd';
import {Link, Route, withRouter, BrowserRouter,  Switch} from "react-router-dom"
import Home from "../pages/home/Home"
import Customer from "../pages/customer/Customer"
const { Header, Footer, Sider, Content } = Layout;

class App extends Component {

  state = {
    currentroute :'1'

  }
  componentDidMount() {
    const route = localStorage.getItem('route');
    console.log(route)

    if (route ==='/'){
      this.setState({currentroute:'1'})
    }else if (route == '/customer'){
      console.log("hello")

      this.setState( prevState => ({
        ...prevState,
        [type]: !prevState[type]
    }));
    }

  }
  render(){
  return (
    <div className="App">
      <BrowserRouter>
      <Layout>
        <Header style={{ padding: 10 }}>

        </Header>
        <Layout>
          <Sider collapsed = "true">
            <Menu
             defaultSelectedKeys = {[this.state.currentroute]}
              mode="inline"
              theme = "dark"

            >
              <Menu.Item key='1' >
                <Link to = "/">
                  <Icon type = "user"/>
                  <span>Home </span>
                </Link>

              </Menu.Item>
              <Menu.Item key='2' >
              <Link to = "/customer">
                  <Icon type = "user"/>
                  <span>Customer </span>
                </Link>
              </Menu.Item>

            </Menu>
          </Sider>
          <Layout>
            <Content style={{ padding: '0 50px', minHeight:400 }}>
            <Switch>
                <Route exact path = "/"  component = {Home}/>
                <Route path = "/customer" component = {Customer}/>
              </Switch>

            </Content>
            <Footer style={{ textAlign: 'center' }}></Footer>
          </Layout>
        </Layout>
      </Layout>
      </BrowserRouter>
    </div>
  );
  }
}

export default  App;
