import {
    withScriptjs,
    withGoogleMap,
    GoogleMap,
    Marker,
    InfoWindow, 
    Circle
  } from "react-google-maps";
  import Geocode from "react-geocode";
  import React, {Component, Fragment} from "react"
  import { compose, withProps, withStateHandlers } from "recompose"
import data from "../../../data/Data";

  class PeopleMap extends Component{
    constructor(props){
      super(props);
      this.state = {
        userInput: "",
        location_data: [],
        openToogleList: Array(data.length).fill(true), 
        defaultMapSize: { lat: -33.797, lng: 151.1 }, 
        windowSize: 10
      }
    }
    componentDidMount(){


      Geocode.setApiKey("AIzaSyAOOgr6Eg-N_Pi3Ic5mNo1EybmjAH3w1D0");

      // set response language. Defaults to english.
      Geocode.setLanguage("en");

      // set response region. Its optional.
      // A Geocoding request with region=es (Spain) will return the Spanish city.
      Geocode.setRegion("aus");
      Geocode.enableDebug();



      // Get latidude & longitude from address.

      for (var i=0; i< data.length; i++){

        Geocode.fromAddress(data[i].address).then(
          response => {
            const { lat, lng } = response.results[0].geometry.location;
            this.setState({
              location_data: [...this.state.location_data, { lat,lng}]
            })
            console.log(lat)
            console.log(lng)


          },
          error => {
            console.error(error);
          }
        );

      }


    }
    componentDidUpdate(previousProps, previousState){
      // if (this.state.defaultCenter !== previousState.defaultCenter && this.state.windowSize !== previousState.windowSize){
        if (this.props.clickable ===true){
          console.log("hello")
          for (var i =0; i < this.state.location_data.length; i++){
            if(data[i].name === this.props.clickedInput.replace(/\s/g, '')){
              console.log('world')
              // let item =this.state.location_data[i]
              // let copyDefaultMapSize= {...this.state.defaultMapSize};
              // copyDefaultMapSize.lat= parseFloat(item.lat);
              // copyDefaultMapSize.lng = parseFloat(item.lng) ;
              this.setState({
                // defaultMapSize: copyDefaultMapSize,
                windowSize:20
              })
              break;
            }
          // }

        }
      }
    }
    changeMapSize(item){


    }

    onOpen(index){
      let newToogleList = [...this.state.openToogleList];
      newToogleList[index] = ! newToogleList[index];

      this.setState({
        openToogleList: newToogleList
      }
      )
    }

    render(){

      return(
        <GoogleMap
      defaultZoom={this.state.windowSize}

      defaultCenter={this.state.defaultMapSize}
    >

      {this.state.location_data.map((item, index)=>{
        if (this.props.clickable === true){

          if (data[index].name == this.props.clickedInput.replace(/\s/g, '')){

            return(
              <Marker
            position={item}
            key = {index}
            onClick={() => this.onOpen(index) }
              >
            {this.state.openToogleList[index] &&
              <Fragment>
              <InfoWindow onCloseClick={()=> this.onOpen(index)}>
                <h3>Name: {data[index].name}</h3>
                {/* <h3>{this.props.clickedInput}</h3> */}
              </InfoWindow>
              <Circle
              defaultCenter={{
                lat: parseFloat(item.lat),
                lng: parseFloat(item.lng)
              }}
              radius={3000}
              options={{strokeColor: "#ff0000"}}
            />
            </Fragment> 

            }

              </Marker>
          )
    }}})}

    </GoogleMap>
      )
    }
  }
  const MapContainer =compose(
    withScriptjs,
    withGoogleMap
  )(PeopleMap);

  export default MapContainer

