import React, {Component} from "react"
import {  Form, Input, Button, Checkbox, Select }  from'antd'
import moment from "moment"
import {withRouter} from "react-router-dom"
import axios from 'axios'
const {Item} = Form
const {Option} = Select
const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
  };
class Scan extends Component{
  constructor(props){
    super(props);
    this.state = {

      item : {
        date: moment().format('MMMM Do YYYY, h:mm:ss a').toString(),
        address: " ",
        email: "",
        isMapOpen: true,
        key: "",
        name: "",
        phone: "",
        status: "CONFIRMED",
        type: ""

      }
    };
    this.onChange= this.onChange.bind(this)
    this.onClick = this.onClick.bind(this)
  }

  onChange = (e) =>{
    let new_item = this.state.item
    new_item.type =   e;
    this.setState({
      item: new_item
    })
  }
  onNameChange = e =>{
    let new_item = this.state.item
    new_item.name =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onAddressChange = e =>{
    let new_item = this.state.item
    new_item.address =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onPhoneChange = e =>{
    let new_item = this.state.item
    new_item.phone =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onEmailChange = e =>{
    let new_item = this.state.item
    new_item.email =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onClick(){

    axios.get(
      "https://fhrs5atn73.execute-api.ap-southeast-2.amazonaws.com/dev",
      {
        key5: `${this.state.date}`,
        key2: `${this.state.address}`,
        key3: `${this.state.email}`,
        key4: `${this.state.isMapOpen}`,
        key1: `${this.state.key}`,
        key6: `${this.state.name}`,
        key7: `${this.state.phone}`,
        key8: `${this.state.status}`,
        key9: `${this.state.type}`
      }
    )
    this.props.history.push("/camera")
  }
    render(){
      const { getFieldDecorator } = this.props.form;
       return (
          <Form
            {...layout}
            name="basic"

          >
            <Item
              label="Name"
              name="name"
              >
              {getFieldDecorator('userName', {
                initialValue: this.state.item.name,
                rules: [{ required: true, message: 'Please input your name!' }],
              })(
              <Input   onChange = {this.onNameChange}/>
              )}

              </Item>

            <Item
              label="Address"
              name="Address"

            >
               {getFieldDecorator('address', {
                 initialValue: this.state.item.address,
                rules: [{ required: true, message: 'Please input your address!' }],
              })(
              <Input  onChange = {this.onAddressChange}/>
              )}  </Item>
            <Item
              label="Phone number"
              name="Phone number"

            >
               {getFieldDecorator('phone', {
                 initialValue: this.state.item.phone,
                rules: [{ required: true, message: 'Please input your phone number!' }],
              })(
              <Input   onChange = {this.onPhoneChange}/>
              )}          
              </Item>
              <Item
              label="Email"
              name="email"
              >
              {getFieldDecorator('email', {
                initialValue: this.state.item.email,
                rules: [{ required: true, message: 'Please input your email!' }],
              })(
              <Input   onChange = {this.onEmailChange}/>
              )}

              </Item>
            <Item
              label="status"
              name="type"
              rules={[
                {
                  required: true,
                  message: 'Please input your name!',
                },
              ]}

            >
              <Select
                value={this.state.item.type}

                onChange={this.onChange}

              >
                <Option value="Employee">Employee</Option>
                <Option value="Non-Employee">Non-Employee</Option>
              </Select>
            </Item>


            <Item {...tailLayout}>
              <Button type="primary" htmlType="submit" onClick = {this.onClick}>
                Submit
              </Button>
            </Item>
          </Form>
        );
  }
}
export default Form.create({ name: 'normal_login' })(withRouter(Scan));
