import React, {Component} from "react"
import data from "../../data/Data"
import moment from "moment"
import {Card, Avatar, Col } from "antd";
const {Meta} = Card
class History extends Component{
    sortData(){
        let sortedData = data.sort(function(a, b) {

            // Compare the 2 dates
            if (moment(a.date).isBefore(b.date)) return -1;
            if (moment(a.date).isAfter(b.date)) return 1;
            return 0;
        });
          return sortedData
    }
    listDate(){
        let sortedData = this.sortData();
        // console.log(sortedData)
        let list_date = []
        for (let i =0 ; i < sortedData.length; i++){
            if (i === 0){
                list_date.push(sortedData[i].date)
            }else{
                // console.log(i)
                // console.log(sortedData[i].date)
                if (!moment(sortedData[i].date).isSame(sortedData[i-1].date)){
                    list_date.push(sortedData[i].date)
                }
            }
        }
        return list_date
    }

    render(){
        let sortedData= this.sortData();
        let listDate = this.listDate();
        return(
            <div>
              {listDate.map((item, index) =>{
                  return(
                      <div>
                      <h3><b>{moment(item).format(("DD-MMMM-YYYY"))} </b></h3>
                      <hr></hr>
                      {sortedData.map((item1, index1) =>{
                          if(moment(item1.date).isSame(item) ){
                              return(
                                  <Card >
                                      <Col lg={10} md={12} sm={24}>
                                      <Meta
                            avatar={<Avatar size = {20} icon= "user" />}
                            title =  {item1.name}
                        /></Col>
                        <Col lg={10} md={12} sm={24}> {moment(item1.date).format("LT")}</Col>
                         </Card>
                              )
                          }
                      })}
                      </div>

                  )
              })}  
            </div>
        )
    }
}
export default History
