import React, {Component} from "react"
import {  Form, Input, Button, Checkbox, Select }  from'antd'
import moment from "moment"
const {Item} = Form
const {Option} = Select
const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
  };
class Scan extends Component{
  constructor(props){
    super(props);
    this.state = {
      emask_data: [],
      item : {
        date: moment().format('MMMM Do YYYY, h:mm:ss a').toString(),
        address: "",
        email: "",
        isMapOpen: true,
        key: "",
        name: "",
        phone: "",
        status: "CONFIRMED",
        type: ""

      }
    };
    this.onChange= this.onChange.bind(this)
  }
  componentDidMount(){
    fetch("https://fhrs5atn73.execute-api.ap-southeast-2.amazonaws.com/dev")
    .then(response => response.json())
    .then(data => this.setState({emask_data:data}))

  }
  onChange = (e) =>{
    let new_item = this.state.item
    new_item.type =   e;
    this.setState({
      item: new_item
    })
  }
  onNameChange = e =>{
    let new_item = this.state.item
    new_item.name =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onAddressChange = e =>{
    let new_item = this.state.item
    new_item.address =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
  onPhoneChange = e =>{
    let new_item = this.state.item
    new_item.phone =   e.currentTarget.value;
    this.setState({
      item: new_item
    })
  }
    render(){
      const { getFieldDecorator } = this.props.form;
       return (
          <Form
            {...layout}
            name="basic"

          >
            <Item
              label="Name"
              name="name"
              >
              {getFieldDecorator('userName', {
                rules: [{ required: true, message: 'Please input your name!' }],
              })(
              <Input  value = {this.state.item.name} onChange = {this.onNameChange}/>
              )}

              </Item>

            <Item
              label="Address"
              name="Address"
              rules={[
                {
                  required: true,
                  message: 'Please input your address!',
                },
              ]}
            >
              <Input value = {this.state.item.address} onChange = {this.onAddressChange}/>
            </Item>
            <Item
              label="Phone number"
              name="Phone number"
              rules={[
                {
                  required: true,
                  message: 'Please input your phone!',
                },
              ]}
            >
              <Input value = {this.state.item.phone} onChange = {this.onPhoneChange}/>
            </Item>
            <Item
              label="status"
              name="type"
              rules={[
                {
                  required: true,
                  message: 'Please input your name!',
                },
              ]}

            >
              <Select
                value={this.state.item.type}

                onChange={this.onChange}

              >
                <Option value="Employee">Employee</Option>
                <Option value="Non-Employee">Non-Employee</Option>
              </Select>
            </Item>


            <Item {...tailLayout}>
              <Button type="primary" htmlType="submit" >
                Submit
              </Button>
            </Item>
          </Form>
        );
  }
}
export default Form.create({ name: 'normal_login' })(Scan);
