import {Layout, Row, Col,Card } from "antd"
import React, {Component} from 'react'
import moment, { months } from "moment"
import { Chart,
    Geom,
    Axis,
    Tooltip, } from "bizcharts";

 const {Content} = Layout 
class MaskChart extends Component{
    constructor(props){
        super(props);
        this.state= {
            emask_data:[],
            month_data:[]
        }
    }
    componentDidMount () {

          fetch("https://fhrs5atn73.execute-api.ap-southeast-2.amazonaws.com/dev")
        .then(response => response.json())
        .then(data => this.setState({emask_data:data}))

      };
      extract_month(){
          var months_data = [
            {
                month: "Jan",
                count_reject:0
            },
            {
                month: "Feb",
                count_reject:0
            },
            {
                month: "Mar",
                count_reject:0
            },
            {
                month: "Apr",
                count_reject:0
            }, 
            {
                month: "May",
                count_reject:0
            },
            {
                month: "Jun",
                count_reject:0
            },
            {
                month: "jul",
                count_reject:0
            },
            {
                month: "Aug",
                count_reject:0
            },
            {
                month: "Sep",
                count_reject:0
            },
            {
                month: "Oct",
                count_reject:0
            }, 
            {
                month: "Nov",
                count_reject:0
            }, 
            {
                month: "Dec",
                count_reject:0
            }
        ];

          for(var i =0; i < this.state.emask_data.length; i++){
            for(var j=0; j < months_data.length; j++){
                var mon= j+1;
                if(this.state.emask_data[i].status ==="ALERTED"){
                    if(parseInt(moment(this.state.emask_data[i].date).format("MM")) == mon ){

                        months_data[j].count_reject = months_data[j].count_reject+1
                        break;
                    }
                }
            }
          }
          return months_data;

      }
    render(){
        var month_data = this.extract_month()
        return(
            <Content>
                <Card>
                <Chart height={400} data={month_data} forceFit>
  <Axis name="month" />
  <Axis name="Number of Alerted" label={{ formatter: val => `${val}` }} />
  <Tooltip crosshairs={{ type : "y" }} />
  <Geom type="line" position="month*temperature" size={2} color={'city'} />
  <Geom type='point' position="month*temperature" size={4} color={'city'} />
</Chart>
                </Card>
            </Content>
        )
    }
}
export default MaskChart;
