export class MultinomialProgram {
  constructor(batchSize, numOutcomes, numSamples) {
    this.variableNames = ['probs'];
    this.outputShape = [batchSize, numSamples];
    this.userCode = "\n      uniform float seed;\n\n      void main() {\n        ivec2 coords = getOutputCoords();\n        int batch = coords[0];\n\n        float r = random(seed);\n        float cdf = 0.0;\n\n        for (int i = 0; i < ".concat(numOutcomes - 1, "; i++) {\n          cdf += getProbs(batch, i);\n\n          if (r < cdf) {\n            setOutput(float(i));\n            return;\n          }\n        }\n\n        // If no other event happened, last event happened.\n        setOutput(float(").concat(numOutcomes - 1, "));\n      }\n    ");
  }

  getCustomSetupFunc(seed) {
    return (gpgpu, webGLProgram) => {
      if (this.seedLoc == null) {
        this.seedLoc = gpgpu.getUniformLocation(webGLProgram, 'seed');
      }

      gpgpu.gl.uniform1f(this.seedLoc, seed);
    };
  }

}
