import React, { useState, useRef, useEffect } from "react";
import Measure from "react-measure";
import { useUserMedia } from "./useUserMedia";
import { useCardRatio } from "./userCardRatio";
import { useOffsets } from "./useOffSet";
import {
  Video,
  Canvas,
  Wrapper,
  Container,
  Flash,
  Overlay,
  Button
} from "./styles";
import { withRouter } from "react-router-dom";
import { OmitProps } from "antd/lib/transfer/renderListBody";
import axios from "axios"
const CAPTURE_OPTIONS = {
  audio: false,
  video: { facingMode: "environment" }
};

function Camera(props) {
  const canvasRef = useRef();
  const videoRef = useRef();

  const [container, setContainer] = useState({ width: 0, height: 0 });
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);
  const [isCanvasEmpty, setIsCanvasEmpty] = useState(true);
  const [isFlashing, setIsFlashing] = useState(false);
  const [linkBlob, setLinkBlob] = useState("");
  const [emask_data, setEmaskData] = useState([])
  const mediaStream = useUserMedia(CAPTURE_OPTIONS);
  const [aspectRatio, calculateRatio] = useCardRatio(1.586);

  const offsets = useOffsets(videoRef.current && videoRef.current.videoWidth,videoRef.current && videoRef.current.videoHeight,container.width,container.height);

  if (mediaStream && videoRef.current && !videoRef.current.srcObject) {
    videoRef.current.srcObject = mediaStream;
  }


  function handleResize(contentRect) {
    setContainer({
      width: contentRect.bounds.width,
      height: Math.round(contentRect.bounds.width / aspectRatio)
    });
  }

  function handleCanPlay() {
    calculateRatio(videoRef.current.videoHeight, videoRef.current.videoWidth);
    setIsVideoPlaying(true);
    videoRef.current.play();
  }

  function handleCapture() {
    const context = canvasRef.current.getContext("2d");

    context.drawImage(
      videoRef.current,
      offsets.x,
      offsets.y,
      container.width,
      container.height,
      0,
      0,
      container.width,
      container.height
    );
    var link = document.createElement("a");
  link.download = "image.png";
    canvasRef.current.toBlob(function(blob){{
        link.href = URL.createObjectURL(blob);
        console.log(blob);
        setLinkBlob(link.href)
        console.log(link.href); // this line should be here
      }}, "image/png", 1);
    setIsCanvasEmpty(false);
    setIsFlashing(true);
  }

  function handleClear() {
    const context = canvasRef.current.getContext("2d");
    context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    setIsCanvasEmpty(false);
    // onClear();
  }
  useEffect(() => { 
    // console.log(props.history.location.state)




    setTimeout(() => { if (isVideoPlaying){
      if(isCanvasEmpty){
        handleCapture();
        handleClear();
        if (sessionStorage.getItem("count_data")){
          const count= parseInt( sessionStorage.getItem("count_data"))
          sessionStorage.setItem("count_data", count+1)
        }else{
          sessionStorage.setItem("count_data", 1)
        }
        axios.post(
          "https://fhrs5atn73.execute-api.ap-southeast-2.amazonaws.com/dev",
          {
            key5: `${props.history.location.state.date}`,
          key2: `${props.history.location.state.address}`,
          key3: `${props.history.location.state.email}`,
          key4: `${props.history.location.state.isMapOpen}`,
          key1: `${props.history.location.state.key}`,
          key6: `${props.history.location.state.name}`,
          key7: `${props.history.location.state.phone}`,
          key8: `${props.history.location.state.status}`,
          key9: `${props.history.location.state.type}`
          }).then(res =>{console.log(res)})

      }



    }}, 2000)

    }
  );



  if (!mediaStream) {
    return null;
  }

  return (
    <Measure bounds onResize={handleResize}>
      {({ measureRef }) => (
        <Wrapper>
          <Container
            ref={measureRef}
            maxHeight={videoRef.current && videoRef.current.videoHeight}
            maxWidth={videoRef.current && videoRef.current.videoWidth}
            style={{
              height: `${container.height}px`
            }}
          >
            <Video
              ref={videoRef}
              hidden={!isVideoPlaying}
              onCanPlay={handleCanPlay}
              autoPlay
              playsInline
              muted
              style={{
                top: `-${offsets.y}px`,
                left: `-${offsets.x}px`
              }}
            />

            <Overlay hidden={!isVideoPlaying} />

            <Canvas
              ref={canvasRef}
              width={container.width}
              height={container.height}
            />

            <Flash
              flash={isFlashing}
              onAnimationEnd={() => setIsFlashing(false)}
            />
          </Container>

          {isVideoPlaying && (
            <Button  onClick={handleCapture }>
               Take a picture
            </Button>
          )}
          {!isCanvasEmpty && 
            <div>
            <h2 style= {{textAlign:"center"}}>Preview</h2>
            <img src = {linkBlob}/>
            </div>
          }


        </Wrapper>
      )}
    </Measure>
  );
}
export default withRouter(Camera)
