    InfoWindow, 
    Circle
  } from "react-google-maps";
  import Geocode from "react-geocode";
  import React, {Component, Fragment} from "react"
  import { compose, withProps, withStateHandlers } from "recompose"
  import { getDistance } from 'geolib';
import data from "../../../data/Data";

  class PeopleMap extends Component{
    constructor(props){
      super(props);
      this.state = {
        userInput: "",
        location_data: [],
        openToogleList: Array(data.length).fill(true), 
        defaultMapSize: { lat: -33.797, lng: 151.1 }, 

      }
    }
    componentDidMount(){


      Geocode.setApiKey("AIzaSyAOOgr6Eg-N_Pi3Ic5mNo1EybmjAH3w1D0");

      // set response language. Defaults to english.
      Geocode.setLanguage("en");

      // set response region. Its optional.
      // A Geocoding request with region=es (Spain) will return the Spanish city.
      Geocode.setRegion("aus");
      Geocode.enableDebug();



      // Get latidude & longitude from address.

      for (var i=0; i< data.length; i++){

        Geocode.fromAddress(data[i].address).then(
          response => {
            const { lat, lng } = response.results[0].geometry.location;
            this.setState({
              location_data: [...this.state.location_data, { lat,lng}]
            })
            console.log(lat)
            console.log(lng)


