import React, {Component} from "react"
import {  Form, Input, Button, Checkbox, Select }  from'antd'
import moment from "moment"
const {Item} = Form
const {Option} = Select
const layout = {
    labelCol: {
      span: 8,
    },
    wrapperCol: {
      span: 16,
    },
  };
  const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
  };
class Scan extends Component{
  constructor(props){
    super(props);
    this.state = {
      emask_data: [],
      item : {
        date: moment().format('MMMM Do YYYY, h:mm:ss a').toString(),
        address: "",
        email: "",
        isMapOpen: true,
        key: "",
        name: "",
        phone: "",
        status: "CONFIRMED",
        type: ""

      }
    };
    this.onChange= this.onChange.bind(this)
  }
  componentDidMount(){
    fetch("https://fhrs5atn73.execute-api.ap-southeast-2.amazonaws.com/dev")
    .then(response => response.json())
    .then(data => this.setState({emask_data:data}))

  }
  onChange = (e) =>{
    let new_item = this.state.item
    new_item.type =   e;
    this.setState({
      item: new_item
    })
  }
    render(){
       return (
          <Form
            {...layout}
            name="basic"
            initialvalues={{
              remember: true,
            }}

          >
            <Item
              label="Name"
              name="name"
              rules={[
                {
                  required: true,
                  message: 'Please input your name!',
                },
              ]}
            >
              <Input />
            </Item>

            <Item
              label="Address"
              name="Address"
              rules={[
                {
                  required: true,
                  message: 'Please input your address!',
                },
              ]}
            >
              <Input />
            </Item>
            <Item
              label="Phone number"
              name="Phone number"
              rules={[
                {
                  required: true,
                  message: 'Please input your phone!',
                },
              ]}
            >
              <Input />
            </Item>
            <Item
              label="status"
              name="type"
              rules={[
                {
                  required: true,
                  message: 'Please input your name!',
                },
              ]}

            >
              <Select
                value={this.state.item.type}
                style={{
                  width: 80,
                  margin: '0 8px',
                }}
                onChange={this.onChange}

              >
                <Option value="Employee">Employee</Option>
                <Option value="Non-Employee">Non-Employee</Option>
              </Select>
            </Item>

            <Item {...tailLayout} name="remember" valuePropName="checked">
              <Checkbox>Remember me</Checkbox>
            </Item>

            <Item {...tailLayout}>
              <Button type="primary" htmlType="submi{t" onClick= {this.onClick}>
                Submit
              </Button>
            </Item>
          </Form>
        );
  }
}
export default Scan
