import {
    withScriptjs,
    withGoogleMap,
    GoogleMap,
    Marker,
    InfoWindow
  } from "react-google-maps";
  import Geocode from "react-geocode";
  import React, {Component} from "react"
  import { compose, withProps, withStateHandlers } from "recompose"
import data from "../../../data/Data";

  class PeopleMap extends Component{
    getLocation(){
      Geocode.setApiKey("xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx");

      // set response language. Defaults to english.
      Geocode.setLanguage("en");

      // set response region. Its optional.
      // A Geocoding request with region=es (Spain) will return the Spanish city.
      Geocode.setRegion("aus");
      Geocode.enableDebug();



      // Get latidude & longitude from address.
      const location_data =[]
      for (var i=0; i< data.length; i++){
        console.log(data[i].address)
        Geocode.fromAddress("Eiffel Tower").then(
          response => {
            const { lat, lng } = response.results[0].geometry.location;
            location_data.push({ lat,lng})
          },
          error => {
            console.error(error);
          }
        );
      }
      return location_data


    }
    componentDidMount(){
      const location_data=this.getLocation();
      for ( var i=0; i< location_data.length; i++){
        console.log(location_data[i])
      }
    }
    render(){
      return(
        <GoogleMap
      defaultZoom={8}
      defaultCenter={{ lat: -34.397, lng: 150.644 }}
    >
      <Marker
        position={{ lat: -34.397, lng: 150.644 }}
        onClick={this.props.onToggleOpen}
      >
        {this.props.isOpen && <InfoWindow onCloseClick={this.props.onToggleOpen}>

        </InfoWindow>}
      </Marker>
    </GoogleMap>
      )
    }
  }
  const MapContainer =compose(
    withStateHandlers(() => ({
      isOpen: false,
    }), {
      onToggleOpen: ({ isOpen }) => () => ({
        isOpen: !isOpen,
      })
    }),
    withScriptjs,
    withGoogleMap
  )(props =>
    <PeopleMap />
  );

  export default MapContainer

