import React, {Component, useState, useEffect} from 'react'
import { Layout, Tabs, Table, Tag} from "antd"
import {withRouter} from "react-router-dom"
import moment from "moment"
const {Content} = Layout;
const {
    TabPane 
  } = Tabs;
  const data= [
      {
        key: "1",
        name: "Victor",
        email: "victor@gmail.com", 
        phone: "04114214124",
        status: "CONFIRMED",
        date: "2020-06-09 12:14:13", 
        type: "Employee"
      },
      {
        key: "2",
        name: "David",
        email: "davidr@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Employee"
      },
      {
        key: "3",
        name: "Eudora",
        email: "eudora@gmail.com", 
        phone: "041112314124",
        status: "CONFIRMED",
        date: "2020-06-09 03:03:12", 
        type: "Employee"
      }, 
      {
        key: "4",
        name: "Alice",
        email: "alicea@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Employee"
      }, 
      {
        key: "5",
        name: "Alice",
        email: "alicea@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Non-Employee"
      }, 
      {
        key: "6",
        name: "Frank",
        email: "franka@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Non-Employee"
      }, 
      {
        key: "7",
        name: "Frank",
        email: "franka@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Non-Employee"
      }, 
      {
        key: "8",
        name: "Frank",
        email: "franka@gmail.com", 
        phone: "041112314124",
        status: "ALERTED",
        date: "2020-06-09 03:03:12", 
        type: "Non-Employee"
      }

    ]
  const column = [
      {
        title : "Name", 
        dataIndex: "name",
        key : "name", 

        sorted: (a,b) => a.name.length -b.name.length,
        sortDirections: ["descend", "ascend"]

      }, 
      {
        title : "Email", 
        dataIndex: "email",
        key : "email",
        sorted: (a,b) => a.email.length- b.email.length,
        sortDirections: ["descend", "ascend"]

      },
      {
        title : "Phone", 
        dataIndex: "phone",
        key : "phone",
        sorted: (a,b) => a.phone- b.phone,
        sortDirections: ["descend", "ascend"]

      },
      {
        title : "Status", 
        dataIndex: "status",
        key : "status", 
        render: status =>{
            let color = "red";
            if (status=== "CONFIRMED"){
                color = "green"
            }else if (status ==="ALERTED"){
                color= "red"
            }
            return (
                <div>
                    <Tag color = {color} >
                        {status}
                    </Tag>
                </div>
            )
        }
      },
      {
        title : "Date", 
        dataIndex: "date",
        key : "date",
        sorted: (a,b) => moment(a.date).isAfter(b.date), 
        sortDirections: ["descend", "ascend"],

      }

  ];
  const [tableData, setTableData] = useState;
  const getData = order => {
    switch (order) {
      case "descend":
        setTableData(data);
        break;
      case "ascend":
        setTableData(data);
        break;
      default:
        setTableData(data);
        break;
    }
  };
  useEffect(() => {
    getData();
  }, []);

  const onChange = (pagination, filters, sorter, extra) => {
    console.log(sorter.order);
    getData(sorter.order);
  };

class SupervisedTable extends Component{



    componentDidMount () {

        localStorage.setItem('route', this.props.location.pathname);

      };
    render(){
        const employees = data.filter(e =>{
            return e.type === "Employee"
        }
        );
        const non_employees = data.filter(e =>{
            return e.type === "Non-Employee"
        }
        )
        return(
            <Content>
                 <Tabs defaultActiveKey = "1">

                    <TabPane key = "1" tab = "Employees">
                        <Table dataSource = {employees}  columns = {column} onChange = {this.onChange}/>
                    </TabPane>
                    <TabPane key = "2" tab = "Non-Enployees">
                        <Table dataSource = {non_employees}  columns = {column}/>
                    </TabPane>
                </Tabs>
            </Content>
        )
    }
}
export default withRouter(SupervisedTable);
