    //           // copyDefaultMapSize.lat= parseFloat(item.lat);
    //           // copyDefaultMapSize.lng = parseFloat(item.lng) ;
    //           this.setState({
    //             // defaultMapSize: copyDefaultMapSize,
    //             windowSize:20
    //           })
    //           break;
    //         }
    //       // }

    //     }
    //   }
    // }
    // changeMapSize(item){


    // }

    onOpen(index){
      let newToogleList = [...this.state.openToogleList];
      newToogleList[index] = ! newToogleList[index];

      this.setState({
        openToogleList: newToogleList
      }
      )
    }
    getIndexPerson(){

      for(var i =0; i< data.length; i++){
        if(data[i].name===this.props.clickedInput.replace(/\s/g, '')){

          // console.log(this.state.clickedInput)
          return i;
        }
      }
    }
    getLocation(item){
      var list_location= []
      for (var i = 0 ; i < this.state.location_data.length; i++){
        if(getDistance(this.state.location_data[i], item)< 1000 && item !== this.state.location_data[i]   ){
          var coord = this.state.location_data[i];
          coord.name= data[i].name
          coord.address= data[i].address
          console.log(data[i].name)

          // console.log(getDistance(this.state.location_data[i],item))
          list_location.push(coord)


        }
      }
      return list_location
    }
    centerChange(){

      if(this.props.clickable === true){

        var index = this.getIndexPerson()
        this.setState({defaultMapSize:this.state.location_data[index]});
      }
    }


    render(){

      return(
        <GoogleMap
    zoom={this.props.windowSize}
      onCenterChanged = {() =>{this.centerChange()}}
      onZoomChanged= {()=> {this.zoomChange()}}
      center={this.state.defaultMapSize}
    >

      {this.state.location_data.map((item, index)=>{
        if (this.props.clickable === true){

          if (data[index].name == this.props.clickedInput.replace(/\s/g, '')){
            var list_location= this.getLocation(item)

            return(
              <div>
              {list_location.map((item1,index1) =>{
                // console.log(data[index1].address)
                return(
                <Marker
            position={{lat : parseFloat(item1.lat), lng: parseFloat(item1.lng) }}
            key = {index1}
            onClick={() => this.onOpen(index1) }
              >
            {this.state.openToogleList[index1] &&
              <Fragment>
              <InfoWindow onCloseClick={()=> this.onOpen(index1)}>
                <div>
                <h3>Name: {item1.name}</h3>
                <h3>Address: {item1.address}</h3>
                </div>
                {/* <Person name = {item1.name} /> */}
              </InfoWindow>

            </Fragment> 

            }

              </Marker>

              )})}
              <Marker
            position={{lat : parseFloat(item.lat), lng: parseFloat(item.lng) }}
            key = {index}
            onClick={() => this.onOpen(index) }
              >
            {this.state.openToogleList[index] &&
              <Fragment>
              <InfoWindow onCloseClick={()=> this.onOpen(index)}>
                <div>
                <h3 style= {{color: "red"}}><b> Name: {data[index].name}</b></h3>
                {/* <h3>{this.props.clickedInput}</h3> */}
                <h3 style= {{color: "red"}}><b> Address: {data[index].address}</b></h3>
                </div>
              </InfoWindow>
              <Circle
              radius= {1000}
              center= {{lat : parseFloat(item.lat), lng: parseFloat(item.lng) }}
              />

            </Fragment> 

            }

              </Marker>
            </div>





          )
    }}})}

    </GoogleMap>
      )
    }
  }
  const MapContainer =compose(
    withScriptjs,
    withGoogleMap
  )(PeopleMap);

  export default MapContainer

